        // If x > 1, then we compute the fraction part of log2(x), which is larger than 0.
        if (x > FIXED_1) {
            for (uint8 i = MAX_PRECISION; i > 0; --i) {
                x = (x * x) / FIXED_1;
                // now 1 < x < 4
                if (x >= FIXED_2) {
                    x >>= 1;
                    // now 1 < x < 2
                    res += ONE << (i - 1);
                }
            }
