pragma solidity ^0.8.10;

import "../../interfaces/ITemporarilyPausableVault.sol";

/**
 * @dev Allows for a contract to be paused during an initial period after deployment, disabling functionality. Can be
 * used as an emergency switch in case a security vulnerability or threat is identified.
 *
 * The contract can only be paused during the Pause Window, a period that starts at deployment. It can also be
 * unpaused and repaused any number of times during this period. This is intended to serve as a safety measure: it lets
 * system managers react quickly to potentially dangerous situations, knowing that this action is reversible if careful
 * analysis later determines there was a false alarm.
 *
 * If the contract is paused when the Pause Window finishes, it will remain in the paused state through an additional
 * Buffer Period, after which it will be automatically unpaused forever. This is to ensure there is always enough time
 * to react to an emergency, even if the threat is discovered shortly before the Pause Window expires.
 *
 * Note that since the contract can only be paused within the Pause Window, unpausing during the Buffer Period is
 * irreversible.
 */
abstract contract TemporarilyPausableVault is ITemporarilyPausableVault {
    // The Pause Window and Buffer Period are timestamp-based: they should not be relied upon for sub-minute accuracy.
    // solhint-disable not-rely-on-time

    uint256 private constant _MAX_PAUSE_WINDOW_DURATION = 90 days;
    uint256 private constant _MAX_BUFFER_PERIOD_DURATION = 30 days;

    uint256 private immutable _pauseWindowEndTime;
    uint256 private immutable _bufferPeriodEndTime;

    bool private _paused;

    constructor(uint256 pauseWindowDuration, uint256 bufferPeriodDuration) {
        require(pauseWindowDuration <= _MAX_PAUSE_WINDOW_DURATION, "MAX_PAUSE_WINDOW_DURATION");
        require(bufferPeriodDuration <= _MAX_BUFFER_PERIOD_DURATION, "MAX_BUFFER_PERIOD_DURATION");

        uint256 pauseWindowEndTime = block.timestamp + pauseWindowDuration;

        _pauseWindowEndTime = pauseWindowEndTime;
        _bufferPeriodEndTime = pauseWindowEndTime + bufferPeriodDuration;
    }

    /**
     * @dev Reverts if the contract is paused.
     */
    modifier whenNotPausedVault() {
        _ensureNotPausedVault();
        _;
    }

    /**
     * @dev Returns the current contract pause status, as well as the end times of the Pause Window and Buffer
     * Period.
     */
    function getPausedStateVault()
        external
        view
        override
        returns (
            bool paused,
            uint256 pauseWindowEndTime,
            uint256 bufferPeriodEndTime
        )
    {
        paused = !_isNotPausedVault();
        pauseWindowEndTime = _getPauseWindowEndTimeVault();
        bufferPeriodEndTime = _getBufferPeriodEndTimeVault();
    }

    /**
     * @dev Sets the pause state to `paused`. The contract can only be paused until the end of the Pause Window, and
     * unpaused until the end of the Buffer Period.
     *
     * Once the Buffer Period expires, this function reverts unconditionally.
     */
    function _setPausedVault(bool paused) internal {
        if (paused) {
            require(block.timestamp < _getPauseWindowEndTimeVault(), "PAUSE_WINDOW_EXPIRED");
        } else {
            require(block.timestamp < _getBufferPeriodEndTimeVault(), "BUFFER_PERIOD_EXPIRED");
        }

        _paused = paused;
        emit PausedStateChangedVault(paused);
    }

    /**
     * @dev Reverts if the contract is paused.
     */
    function _ensureNotPausedVault() internal view {
        require(_isNotPausedVault(), "PAUSED");
    }

    /**
     * @dev Reverts if the contract is not paused.
     */
    function _ensurePausedVault() internal view {
        require(!_isNotPausedVault(), "NOT_PAUSED");
    }

    /**
     * @dev Returns true if the contract is unpaused.
     *
     * Once the Buffer Period expires, the gas cost of calling this function is reduced dramatically, as storage is no
     * longer accessed.
     */
    function _isNotPausedVault() internal view returns (bool) {
        // After the Buffer Period, the (inexpensive) timestamp check short-circuits the storage access.
        return block.timestamp > _getBufferPeriodEndTimeVault() || !_paused;
    }

    // These getters lead to reduced bytecode size by inlining the immutable variables in a single place.

    function _getPauseWindowEndTimeVault() private view returns (uint256) {
        return _pauseWindowEndTime;
    }

    function _getBufferPeriodEndTimeVault() private view returns (uint256) {
        return _bufferPeriodEndTime;
    }
}

