contracts/RequiemFactory.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "./interfaces/IRequiemFactory.sol";
import "./RequiemPair.sol";

contract RequiemFactory is IRequiemFactory {
    address public feeTo;
    address public formula;
    uint256 public protocolFee;
    address public feeToSetter;

    mapping(bytes32 => address) private _pairSalts;
    address[] public allPairs;
    mapping(address => uint64) private _pairs;

    constructor(address _feeToSetter, address _formula) {
        feeToSetter = _feeToSetter;
        formula = _formula;
    }

    function isPair(address b) external view returns (bool) {
        return _pairs[b] > 0;
    }

    function allPairsLength() external view returns (uint256) {
        return allPairs.length;
    }

    function getPair(
        address tokenA,
        address tokenB,
        uint32 tokenWeightA,
        uint32 swapFee
    ) external view returns (address pair) {
        (address token0, address token1, uint32 tokenWeight0) = tokenA < tokenB ? (tokenA, tokenB, tokenWeightA) : (tokenB, tokenA, 100 - tokenWeightA);
        bytes32 salt = keccak256(abi.encodePacked(token0, token1, tokenWeight0, swapFee));
        pair = _pairSalts[salt];
    }

    function createPair(
        address tokenA,
        address tokenB,
        uint32 tokenWeightA,
        uint32 swapFee
    ) external returns (address pair) {
        require(tokenA != tokenB, "RLP: IDENTICAL_ADDRESSES");
        require(tokenWeightA >= 2 && tokenWeightA <= 98 && (tokenWeightA % 2) == 0, "RLP: INVALID_TOKEN_WEIGHT");
        // swap fee from [0.01% - 20%]
        require(swapFee >= 1 && swapFee <= 2000, "RLP: INVALID_SWAP_FEE");
        (address token0, address token1, uint32 tokenWeight0) = tokenA < tokenB ? (tokenA, tokenB, tokenWeightA) : (tokenB, tokenA, 100 - tokenWeightA);
        require(token0 != address(0), "RLP: ZERO_ADDRESS");
        // single check is sufficient
        bytes memory bytecode = type(RequiemPair).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(token0, token1, tokenWeight0, swapFee));
        require(_pairSalts[salt] == address(0), "RLP: PAIR_EXISTS");
        assembly {
            pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        IRequiemPair(pair).initialize(token0, token1, tokenWeight0, swapFee);
        _pairSalts[salt] = address(pair);
        allPairs.push(pair);
        uint64 weightAndFee = uint64(swapFee);
        weightAndFee |= uint64(tokenWeight0) << 32;
        _pairs[address(pair)] = weightAndFee;
        emit PairCreated(token0, token1, pair, tokenWeight0, swapFee, allPairs.length);
    }

    function setFeeTo(address _feeTo) external {
        require(msg.sender == feeToSetter, "RLP: FORBIDDEN");
        feeTo = _feeTo;
    }

    function setFeeToSetter(address _feeToSetter) external {
        require(msg.sender == feeToSetter, "RLP: FORBIDDEN");
        feeToSetter = _feeToSetter;
    }

    function setProtocolFee(uint256 _protocolFee) external {
        require(msg.sender == feeToSetter, "RLP: FORBIDDEN");
        require(_protocolFee == 0 || (_protocolFee >= 10000 && _protocolFee <= 100000), "RLP: Invalid Protocol fee");
        protocolFee = _protocolFee;
    }

    function getWeightsAndSwapFee(address pair)
        public
        view
        returns (
            uint32 tokenWeight0,
            uint32 tokenWeight1,
            uint32 swapFee
        )
    {
        uint64 weightAndFee = _pairs[pair];
        if (weightAndFee > 0) {
            swapFee = uint32(weightAndFee);
            tokenWeight0 = uint32(weightAndFee >> 32);
            tokenWeight1 = 100 - tokenWeight0;
        } else {
            // Default is 0.3%
            return (50, 50, 30);
        }
    }
}

contracts/ProtocolFeeRemover.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.10;

import "./interfaces/IRequiemPair.sol";
import "./libraries/TransferHelper.sol";
import "./interfaces/ERC20/IERC20.sol";

contract ProtocolFeeRemover {
    address public receiver;
    address public governance;

    event RemoveLiquidity(address indexed pair, uint256 token0, uint256 token1);
    event ChangeGovernance(address changeValue);
    event ChangeReceiver(address changeValue);

    constructor() {
        governance = msg.sender;
    }

    function setReceiver(address _receiver) external {
        require(msg.sender == governance, "ProtocolFeeRemover: FORBIDDEN");
        receiver = _receiver;
        emit ChangeReceiver(_receiver);
    }

    function setGovernance(address _governance) external {
        require(msg.sender == governance, "ProtocolFeeRemover: FORBIDDEN");
        governance = _governance;
        emit ChangeGovernance(_governance);
    }

    function transfer(address _token, uint256 _value) external {
        require(msg.sender == governance, "ProtocolFeeRemover: FORBIDDEN");
        require(receiver != address(0), "ProtocolFeeRemover: Invalid Receiver address");
        TransferHelper.safeTransfer(_token, receiver, _value);
    }

    function transferAllTokens(address[] calldata _tokens) external {
        require(msg.sender == governance, "ProtocolFeeRemover: FORBIDDEN");
        require(receiver != address(0), "ProtocolFeeRemover: Invalid Receiver address");

        for (uint256 i = 0; i < _tokens.length; i++) {
            uint256 _balance = IERC20(_tokens[i]).balanceOf(address(this));
            TransferHelper.safeTransfer(_tokens[i], receiver, _balance);
        }
    }

    function remove(address[] calldata pairs) external {
        address _receiver = receiver;
        // save gas
        require(_receiver != address(0), "ProtocolFeeRemover: Invalid Receiver address");
        for (uint256 i = 0; i < pairs.length; i++) {
            IRequiemPair pair = IRequiemPair(pairs[i]);
            uint256 liquidity = pair.balanceOf(address(this));
            if (liquidity > 0) {
                pair.transfer(address(pair), liquidity);
                (uint256 amount0, uint256 amount1) = pair.burn(_receiver);
                emit RemoveLiquidity(address(pair), amount0, amount1);
            }
        }
    }
}

